mapper.select:
SELECT * FROM ${tableName}


        AND src_ip BETWEEN #{src_ip_start} AND #{src_ip_end}


        AND src_port BETWEEN #{src_port_start} AND #{src_port_end}


        AND dst_ip BETWEEN #{dst_ip_start} AND #{dst_ip_end}


        AND detected_name LIKE #{detected_name}


        AND level = #{level}


        AND action = #{action}


        AND time BETWEEN #{startDate} AND #{endDate}


ORDER BY time ASC, log_index ASC

SELECT * FROM ${tableName}


        AND time BETWEEN #{startDate} AND #{endDate}


ORDER BY log_index DESC

	SELECT * FROM ${tableName}


        	AND time BETWEEN #{startDate} AND #{endDate}


	ORDER BY log_index DESC
